@charset "utf-8";

*{box-sizing: border-box;}
html{width:100%;height:100%;font-size:100px;}
body{overflow: inherit;width:100%;height:100%;min-width:1200px;padding:20px;box-sizing:border-box;background: #fafaff;}
body.on{overflow: hidden;}
button{display: inline-block;background: transparent;border:0;outline: 0;}
table{width:100%;}

.text-left{text-align: left !important;}
.text-center{text-align: center !important;}
.text-right{text-align: right !important;}
.simple-txt{font-size:13px;padding:5px 0;}

.default-color{background:#fff;}
.text-red{color:#ff4947 !important;}
.page-tit{font-size:35px;padding-bottom:20px;}

.button-group{padding:20px 0;text-align: center;font-size:0;}
.button{display:inline-block;text-align:center;font-weight:400;border-radius:5px;}
.button + .button{margin-left:10px;}
.button:first-child{margin-left:0;}
.button-tiny{width:50px;line-height: 30px;font-size:14px;}
.button-small{width:100px;line-height: 30px;font-size:14px;}
.button-default{width:150px;line-height: 40px;font-size:15px;}

.primary-button{background:#333;color:#fff;}
.cancel-button{background:#f3f3f3;color:#898989;}
.warring-button{background:#ff4947;color:#fff;}
.secondary-button{background:#5a6268;color:#fff;}
.blue-button{background:#29a2d9;color:#fff;}
.green-button{background:#00d037;color:#fff;}
.green-button2{background:#6dd6d2;color:#fff;}
.tab{margin-top:20px;}
.tab a{display: inline-block;font-size: 14px;width: 120px;text-align: center;color: #333;line-height: 40px;border: 1px solid #333;margin-left: -1px;}
.tab a:first-child{margin-left:0;}
.tab a:hover,
.tab a.active{background: #333; color:#fff;}

/**
 * input number에서 숫자 증감 버튼 제거
 * 엄상현 2020.07.03
 * https://web-atelier.tistory.com/16
 */
input[type='number'].onlyNumber::-webkit-outer-spin-button,
input[type='number'].onlyNumber::-webkit-inner-spin-button {
	-webkit-appearance: none;
	margin: 0;
}

/* popup */
#layer-img{display:none;position: fixed;width: 100%;height: 100%;top: 0;left: 0;background: rgba(0,0,0,0.7);z-index: 1000;}
#layer-img .img{position:absolute;max-width:90%;max-height: 90%;top:50%;left:50%;transform: translate(-50%, -50%);}
#layer-img button{display: inline-block;width:50px;height:50px;position: absolute;top:20px;right:20px;overflow:hidden;text-indent: -9999999px;}
#layer-img button:before{content:"";display:inline-block;position:absolute;top: 0;right: 25px;width:2px; height:50px;background:#fff;transform: rotate(45deg);}
#layer-img button:after{content:"";display:inline-block;width:2px;position:absolute;top:0;left:25px; height:50px;background:#fff;transform: rotate(-45deg);}

/* form */
.cmm-form{padding:20px;box-sizing: border-box;}
.cmm-form .form{position:relative;overflow:hidden;border-bottom:0.01rem solid #f3f3f3;}
.cmm-form .form:after{content: "";opacity:0;display:inline-block;position:absolute;left:50%;transform: translateX(-50%);bottom:0;width:0;height:2px;background:#333;-moz-transition: all 0.5s;-webkit-transition: all 0.5s;

right: 25px;width:2px; height:50px;background:#fff;transform: rotate(45deg);}
#layer-img button:after{content:"";display:inline-block;width:2px;position:absolute;top:0;left:25px; height:50px;background:#fff;transform: rotate(-45deg);}

/* form */
.cmm-form{padding:20px;box-sizing: border-box;}
.cmm-form .form{position:relative;overflow:hidden;border-bottom:0.01rem solid #f3f3f3;}
.cmm-form .form:after{content: "";opacity:0;display:inline-block;position:absolute;left:50%;transform: translateX(-50%);bottom:0;width:0;height:2px;background:#333;-moz-transition: all 0.5s;-webkit-transition: all 0.5s;-ms-transition: all 0.5s;-o-transition: all 0.5s;transition: all 0.5s;}
.cmm-form .form.active:after{opacity:1;width:100%;height:2px;-moz-transition: all 0.5s;-webkit-transition: all 0.5s;-ms-transition: all 0.5s;-o-transition: all 0.5s;transition: all 0.5s;}
.cmm-form .label{display:block;padding:10px 20px;font-size:0.14px;font-weight:500;}
.cmm-form .label .add-button{float: right;display: inline-block;font-size:16px;}
.cmm-form .form label{position: absolute;top:-9999999px;left:-999999px;}
.cmm-form .form input[type="text"],
.cmm-form .form input[type="password"]{width:100%;height:40px;color:#898989;font-size:20px;border:0;box-sizing: border-box;padding:0 20px;}
.cmm-form .form input::placeholder{color: #ccc;font-size:20px;font-weight:400;}
.cmm-form .form input:focus{border:0;outline: none;}
.cmm-form.error .form{border-color:#ff4947;}
.cmm-form.error .form input{color:#ff4947;}
.cmm-form.error .form input::placeholder{color:#ff4947;}

input.apple-switch {-moz-transition: all 0.5s;-webkit-transition: all 0.5s;-ms-transition: all 0.5s;-o-transition: all 0.5s;transition: all 0.5s;position: relative;vertical-align:middle;-webkit-appearance: none;outline: none;width: 50px;height: 25px;background-color: #fff;border: 1px solid #D9DADC;border-radius: 50px;box-shadow: inset -20px 0 0 0 #fff;}
