/* form style */
.checkbox{position: relative;}
.checkbox input[type=checkbox]{display:inline-block;position:absolute;opacity:0;}
.checkbox label{display:inline-block;position:relative;padding-left:0;color:#515151;width:18px;overflow:hidden;font-size:15px;text-align:left;font-weight:500;box-sizing:border-box;cursor:pointer;}
.checkbox label:before{content:'';display:inline-block;position:absolute;top:2px;left:0;width:18px;height:18px;border:1px solid #999;box-sizing:border-box;}
.checkbox label:after{content:"";display:none;position: absolute;top:3px;left:5px;width:4px;height:10px;border-right:4px solid #ff4947;border-bottom:4px solid #ff4947;transform:rotate(45deg);}
.checkbox input[type=checkbox]:checked + label:after{display:inline-block;}

.radio{display:inline-block;margin:0 10px;}
.radio input[type=radio]{display:inline-block;position:absolute;opacity:0;}
.radio label{display:inline-block;position:relative;top:inherit;left:inherit;padding-left:25px;font-size:14px;color:#333;font-weight:400;cursor:pointer;}
.radio label:before{content:'';position:absolute;top:50%;left:0;margin-top:-10px;width:20px;height:20px;border:1px solid #ccc;border-radius:100%;box-sizing:border-box;}
.radio label:after{content:'';display:none;position:absolute;top:50%;left:5px;margin-top:-5px;width:10px;height:10px;background:#29a2d9;border-radius:100%;}
.radio input[type=radio]:checked + label:before{border-color:#29a2d9;}
.radio input[type=radio]:checked + label:after{display:inline-block;}

