// 비밀번호 체크
function chkLoginPWD2(pw)
{
	if(isBlank(pw) === false)
	{
		var num = pw.search(/[0-9]/);
		var eng = pw.search(/[a-zA-Z]/);
		var spe = pw.search(/[~,!,@,#,$,%,^,&,*,(,),\\,-,=,_,+,|,.]/);

		if(inputLength(pw, 15, 6) != "2")
		{
			return "비밀번호는 6~15자 이내로 작성해주시기 바랍니다.";
		}

		if((num < 0 && eng < 0) || (num < 0 && spe < 0) || (eng < 0 && spe < 0))
		{
			return "영문, 숫자, 특수문자 3가지 중 2가지 이상을 조합해주세요.";
		}

		return "SUCCESS";
	}
	else
	{
		return "비밀번호를 입력해주세요";
	}
}

