function numberFormat(num)
{
	var num_str = num.toString();
	var result = "";

	for (var i=0; i<num_str.length; i++)
	{
		var tmp = num_str.length - (i+1);

		if (((i % 3) == 0) && (i != 0))
			result = ","+ result;

		result = num_str.charAt(tmp) + result;
	}

	return result;
}

